# Generated by the windmill services transformer
from windmill.authoring import WindmillTestClient

def test_dragdropunit():
    client = WindmillTestClient(__name__)

    client.open(url=u'http://tutorial.getwindmill.com/windmill-unittests/jqueryui152/demos/functional/index.html')
    client.waits.forPageLoad(timeout=u'40000')
    client.waits.forElement(link=u'Draggable', timeout=u'40000')
    #test the draggable widgets
    client.click(link=u'Draggable')
    client.dragDropElem(pixels=u'(200,0)', id=u'dragImage')
    client.waits.sleep(milliseconds=u'500')
    #client.scroll(coords=u'(0,550)')
    client.waits.sleep(milliseconds=u'500')
    client.dragDropElem(pixels=u'(300,0)', id=u'divDrag')
    #client.asserts.assertProperty(validator=u'style.left|289px', id=u'divDrag')
    client.dragDropElem(xpath=u'/html/body/div/div[2]/div/div[2]/form/div[2]/table/tbody/tr/td[2]/div/div[3]/div[3]/div/div', pixels=u'(257,0)')
    #client.asserts.assertProperty(validator=u'style.left|246px', id=u'draggable-handle-div')
    client.dragDropElem(link=u'Drag me', pixels=u'(420,0)')
    #client.asserts.assertProperty(validator=u'style.left|409px', id=u'draggable-dragPrevention')
    #test the droppable widgets
    client.click(link=u'Droppable')
    client.click(link=u'Droppable')
    client.waits.sleep(milliseconds=u'500')
    #client.waits.forPageLoad(timeout=u'20000')
    client.dragDropElem(xpath=u'/html/body/div/div[2]/div/div[2]/form/div[2]/table/tbody/tr/td[2]/div/div/div[3]/div/ul/li[2]/img', pixels=u'(310,0)')
    client.waits.sleep(milliseconds=u'800')
    client.asserts.assertProperty(xpath=u'/html/body/div/div[2]/div/div[2]/form/div[2]/table/tbody/tr/td[2]/div/div/div[3]/div/div/img', validator=u'src|templates/images/P1010039.JPG')
    client.dragDropElemToElem(xpath=u'/html/body/div/div/div/div[2]/form/div[2]/table/tbody/tr[1]/td[2]/div/div/div[3]/div/ul/li/img', optxpath=u'/html/body/div/div/div/div[2]/form/div[2]/table/tbody/tr[1]/td[2]/div/div/div[3]/div/div')    
    client.waits.sleep(milliseconds=u'500')
    client.asserts.assertProperty(xpath=u"/html/body/div/div[2]/div/div[2]/form/div[2]/table/tbody/tr/td[2]/div/div/div[3]/div/div/img", validator=u'src|P1010020.JPG')
    #client.scroll(coords=u'(0,500)')
    client.dragDropElemToElem(xpath=u"/html/body/div/div[2]/div/div[2]/form/div[2]/table/tbody/tr/td[2]/div/div[2]/div[3]/div", optxpath=u"/html/body/div/div[2]/div/div[2]/form/div[2]/table/tbody/tr/td[2]/div/div[2]/div[3]/div[4]")
    client.waits.sleep(milliseconds=u'500')
    client.dragDropElemToElem(xpath=u"/html/body/div/div[2]/div/div[2]/form/div[2]/table/tbody/tr/td[2]/div/div[2]/div[3]/div[2]", optxpath=u"/html/body/div/div[2]/div/div[2]/form/div[2]/table/tbody/tr/td[2]/div/div[2]/div[3]/div[4]")
    client.waits.sleep(milliseconds=u'500')
    client.dragDropElem(xpath=u'/html/body/div/div[2]/div/div[2]/form/div[2]/table/tbody/tr/td[2]/div/div[2]/div[3]/div', pixels=u'(0,250)')
    client.waits.sleep(milliseconds=u'500')
    client.asserts.assertText(xpath=u'/html/body/div/div[2]/div/div[2]/form/div[2]/table/tbody/tr/td[2]/div/div[2]/div[3]/div[4]', validator=u'Dropped!')
    client.dragDropElem(xpath=u'/html/body/div/div[2]/div/div[2]/form/div[2]/table/tbody/tr/td[2]/div/div[2]/div[3]/div[2]', pixels=u'(0,200)')
    client.waits.sleep(milliseconds=u'500')
    client.asserts.assertText(xpath=u'/html/body/div/div[2]/div/div[2]/form/div[2]/table/tbody/tr/td[2]/div/div[2]/div[3]/div[4]', validator=u'Dropped! Dropped!')
    #test the sortable widgets
    client.click(link=u'Sortable')
    client.click(link=u'Sortable')
    client.waits.sleep(milliseconds=u'500')
    #client.waits.forPageLoad(timeout=u'20000')
    client.asserts.assertJS(js=u"_w.document.getElementById('user_Susan').parentNode.childNodes.length == 11;")
    client.dragDropElem(pixels=u'(160,0)', id=u'user_Susan')
    client.asserts.assertJS(js=u"_w.document.getElementById('user_Susan').parentNode.childNodes.length == 12;")
    #test the dialog widgets
    client.click(link=u'Dialog')
    client.click(link=u'Dialog')
    #client.waits.forPageLoad(timeout=u'20000')
    client.dragDropElem(xpath=u"/html/body[@id='functional_demos']/div[3]/div[1]/div[1]", pixels=u'(150,0)')
    #client.asserts.assertProperty(xpath=u'/html/body/div[3]', validator=u'style.left|455px')
    #test the slider widgets
    client.click(link=u'Slider')
    client.click(link=u'Slider')
    client.waits.sleep(milliseconds=u'500')
    #client.waits.forPageLoad(timeout=u'20000')
    client.dragDropElem(xpath=u'/html/body/div/div[2]/div/div[2]/form/div[2]/table/tbody/tr/td[2]/div/div/div[3]/div/a/div', pixels=u'(80,0)')
    #client.asserts.assertProperty(xpath=u'/html/body/div/div[2]/div/div[2]/form/div[2]/table/tbody/tr/td[2]/div/div/div[3]/div/a/div', validator=u'style.left|79px')
    client.dragDropElem(xpath=u'/html/body/div/div[2]/div/div[2]/form/div[2]/table/tbody/tr/td[2]/div/div[2]/div[3]/div/a/div', pixels=u'(50,0)')
    client.waits.sleep(milliseconds=u'500')
    client.dragDropElem(xpath=u'/html/body/div/div[2]/div/div[2]/form/div[2]/table/tbody/tr/td[2]/div/div[2]/div[3]/div/a[2]/div', pixels=u'(-20,0)')
    #client.asserts.assertProperty(xpath=u'/html/body/div/div[2]/div/div[2]/form/div[2]/table/tbody/tr/td[2]/div/div[2]/div[3]/div/a/div', validator=u'style.left|49px')
    #client.asserts.assertProperty(xpath=u'/html/body/div/div[2]/div/div[2]/form/div[2]/table/tbody/tr/td[2]/div/div[2]/div[3]/div/a[2]/div', validator=u'style.left|81px')
    client.click(link=u'Draggable')
